package my_package;

public class ArrayIterate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {5,6,1,2,3};
		for(int i=0;i<a.length;i++) {
			System.out.println(a[i]);
		}
		int j=2;
		System.out.println();
		System.out.println(a[j]);

	}

}
