package my_package;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		System.out.println(a);

		a+=2;
		System.out.println(a);

		a-=2;
		System.out.println(a);

		a*=2;
		System.out.println(a);

		a/=2;
		System.out.println(a);

		a%=2;
		System.out.println(a);
		//Unary operators
		a++;
		System.out.println(a);
		a--;
		System.out.println(a);
		--a;
		System.out.println(a);
		++a;
		System.out.println(a);

	}

}
