package my_package;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=6,b=21;
		int c=a&b;//and
		int d=a|b;//or
		int e=a^b;//xor
		int f=~a;//complement
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);



	}

}
