package my_package;

public class ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=17;
		if(a==0) {
			System.out.println("It is zero.");
		}
		else if(a%2==0) {
			System.out.println("It is even.");
		}
		else if(a%2!=0) {
			System.out.println("It is odd");
		}
		else {
			System.out.println("not an integer.");

		}

	}

}
